mapper.select:
SELECT id, entity_type, entity_id, name, state, type, value, version
FROM archive_price
WHERE id =#{id}

mapper.insert:
INSERT INTO archive_price (entity_type, entity_id, name
, state
, type
, value)
VALUES (#{entityType},#{entityId},#{name}
,#{state}
,#{type}
,#{value})

mapper.update:
UPDATE archive_price
entity_type=#{entityType}, entity_id=#{entityId}, name=#{name}, state=#{state}, type=#{type}, value=#{value},
WHERE id=#{id}

mapper.select:
SELECT id, entity_type, entity_id, name, state, type, value, version
FROM archive_price


		state = #{state}


		AND type = #{type}


		AND name = #{name}
